import { createRoot } from 'react-dom/client';

const TextRing = ({children, side}) => {
    const CHARS = children.split('')
    const INNER_ANGLE = 360 / CHARS.length
    return (
      <span
        className="text-ring"
        style={{
          '--total': CHARS.length,
          '--radius': side / Math.sin(INNER_ANGLE / (180 / Math.PI))
        }}
      >
        {CHARS.map((char, index) => (
          <span style={{'--index': index }}>
            {char}
          </span>
        ))}
      </span>
    )
  }
  
  const AppText = () => {
    return (
      <TextRing side={1.6}>
        Видеонаблюдение в доме •&nbsp;
      </TextRing>
    )
  }
  
  createRoot (<AppText/>, document.querySelector('.main-header__item__sec-cam'));
