import "./style.css";
import React, { useRef, useState } from 'react';
import { useResize } from "../components/resize/resize.js";
import { ReactComponent as MobileNavbarImg } from '../img/icons/menu.svg';

const Navbar = () => {

    const { isScreenMd } = useResize();

    const [isOpened, setIsOpened] = useState(false);

    const handlerMenu = () => setIsOpened(!isOpened) ;
    console.log(isOpened, setIsOpened);
    const addClassOpened = () => (isOpened) ? 'opened' : '';


   /* const handlerMenu = (e) => {
        const t = e.target;
        const opened = t.classList.contains('opened');
        const body = document.body;
         
        const handleOpen = () => {
            t.classList.add('opened');
            t.nextSibling.classList.add('opened');
            //body.addEventListener('click', () => handleClose());
        }
        const handleClose = () => {
            t.classList.remove('opened');
            t.nextSibling.classList.remove('opened');
            body.removeEventListener('click', () => handleClose());
        }

        if(!opened) {
            handleOpen();
        }else{
            handleClose();
        } 
    }*/

    const ResizeNavbar = () => {
        if (!isScreenMd){
        return (
            <div className={`header-navbar__mobile-nav-list__icon ${ addClassOpened() }`} onClick={handlerMenu}>
                <MobileNavbarImg />
            </div>
        );
        }
    };

    return (
        <nav className="nav">
            <div className="header-navbar">
                <div className="header-navbar-wrapper">
                        <ResizeNavbar />
                    <ul className={`header-navbar__nav-list ${ addClassOpened() }`}>
                        <li className="header-navbar__nav-list__item"><a href="#!" className="header-navbar__nav-list__link">О компании</a></li>
                        <li className="header-navbar__nav-list__item"><a href="#!" className="header-navbar__nav-list__link">Новости</a></li>
                        <li className="header-navbar__nav-list__item"><a href="#!" className="header-navbar__nav-list__link">Информация</a></li>
                        <li className="header-navbar__nav-list__item"><a href="#!" className="header-navbar__nav-list__link">Контакты</a></li>
                    </ul>
                </div>
            </div>
        </nav>
    );
  }
  
  export default Navbar;
