import React, { useEffect, useState, createContext } from 'react';
import getList from "../../../data/news.json";
import NewsItem from "./components/newsItem.jsx";
import "./style.css";

export const NewsContext = createContext();

const NewsList = () => {

   const [list, setList] = useState(getList);

    return(
        <div className='news-list'>
            <div className="news-list-full-size">
                <div className="container">
                    <div className="news-list__wrapper">
                        <h1>НОВОСТИ</h1>
                        <ul className="news-list__list">
                            {list.slice().reverse().map((item) => {
                                const { id, author, title, description, url, urlToImage, publishedAt, content } = item;
                                
                                return (
                                <>
                                <NewsItem key={item.id} data={item} />
                                <NewsContext.Provider
                                        value={{
                                            id,
                                            author, 
                                            title, 
                                            description, 
                                            url, 
                                            urlToImage, 
                                            publishedAt, 
                                            content
                                        }}
                                    >
                                    </NewsContext.Provider>
                                </>
                                )
                            })}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default NewsList;
